mapper.select:
SELECT e.emp_id AS empId, e.emp_name AS empName, e.emp_no AS empNo, e.emp_address AS empAddress, e.emp_email AS empEmail, e.emp_joined AS empJoined, e.annual_leave AS annualLeave, e.used_annual_leave as usedAnnualLeave, e.emp_pwd AS empPwd, e.profile AS profile, e.emp_phone AS empPhone, e.dept_code AS deptCode, d.dept_name AS deptName, e.position_value AS positionValue, p.position_name AS positionName, e.view_auth AS viewAuth FROM employee e JOIN tbl_dept d ON e.dept_code = d.dept_code JOIN tbl_position p ON e.position_value = p.position_value
AND e.dept_code = #{sendDept}
AND e.emp_name LIKE CONCAT('%', #{sendSearchEmpName}, '%')

mapper.update:
UPDATE employee SET emp_name = #{empName}, emp_no = #{empNo}, emp_address = #{empAddress}, emp_email = #{empEmail}, emp_joined = #{empJoined}, annual_leave = #{annualLeave}, profile = #{profile}, emp_phone = #{empPhone}, dept_code = #{deptCode}, position_value = #{positionValue}, view_auth = #{viewAuth}
, emp_pwd = #{empPwd}
WHERE emp_id = #{empId}

mapper.select:
SELECT a.att_date AS attDate, a.att_time AS attTime, a.att_status AS attStatus, e.emp_id AS empId
FROM tbl_attendance a
LEFT OUTER JOIN employee e ON e.emp_id = a.emp_id
WHERE DATE(a.att_date) = #{selectedDay}

    AND e.dept_code = #{searchDept}


    AND e.emp_name LIKE CONCAT('%', #{searchEmpName}, '%')

LIMIT #{offset}, #{size}

SELECT count(*)
FROM tbl_attendance a
LEFT OUTER JOIN employee e ON e.emp_id = a.emp_id
WHERE DATE(a.att_date) = #{selectedDay}

    AND e.dept_code = #{searchDept}


    AND e.emp_name LIKE CONCAT('%', #{searchEmpName}, '%')

mapper.update:
UPDATE tbl_attendance
SET
    att_status = #{selectedStatus}
WHERE emp_id = #{empId}
  AND att_date = #{attDate}

mapper.select:
SELECT prod_id, prod_name, prod_price, prod_description, prod_image_path, emp_id, prod_category,prod_status
FROM tbl_product
WHERE prod_id = #{id}
