mapper.select:
SELECT COUNT(*)
FROM tbl_pm pm
WHERE pm.emp_id = #{empId}

SELECT pm_status
FROM tbl_approval
WHERE pm_id = #{pmId}

mapper.update:
UPDATE tbl_approval
SET pm_status = #{status}
WHERE pm_id = #{pmId} AND emp_id = #{empId};

mapper.select:
SELECT pm_status
FROM tbl_approval
WHERE pm_id = #{pmId}

SELECT approval_order
FROM tbl_approval
WHERE pm_id = #{pmId} AND emp_id = #{empId}

mapper.update:
UPDATE tbl_approval
SET pm_status = #{status}, reason = #{reason}
WHERE pm_id = #{pmId} AND emp_id = #{empId};

UPDATE tbl_approval
SET pm_status = #{status}
WHERE pm_id = #{pmId};

mapper.select:
SELECT COUNT(*)
FROM tbl_approval
WHERE pm_status = '완료' AND emp_id = #{empId}

SELECT COUNT(*)
FROM tbl_approval
WHERE pm_status = '진행 중' AND emp_id = #{empId}

SELECT COUNT(*)
FROM tbl_approval
WHERE pm_status = '반려' AND emp_id = #{empId}

SELECT leave_type
FROM tbl_pm
WHERE pm_id = #{pmId}

SELECT leave_date
FROM tbl_pm
WHERE pm_id = #{pmId}

mapper.update:
UPDATE tbl_pm
SET leave_type = #{leaveType}
WHERE pm_id = #{pmId}

UPDATE tbl_pm
SET leave_date = #{leaveDate}
WHERE pm_id = #{pmId}

mapper.select:
SELECT MIN(approval_order)
FROM tbl_approval
WHERE pm_id = #{pmId} AND pm_status IN ('대기 중', '진행 중');

SELECT emp_id
FROM tbl_approval
WHERE pm_id = #{pmId}

mapper.update:
UPDATE tbl_approval
SET pm_status = '진행 중'
WHERE pm_id = #{pmId} AND pm_status = '대기 중';

mapper.select:
