mapper.select:
SELECT MAX(approval_order)
FROM tbl_approval
WHERE pm_id = #{pmId}

SELECT approval_order
FROM tbl_approval
WHERE pm_id = #{pmId}
  AND emp_id = #{currentEmpId}

SELECT reason
FROM tbl_approval
WHERE pm_id = #{pmId}
  AND emp_id = #{currentEmpId}
  AND reason IS NOT NULL;

