mapper.select:
SELECT receiver_emp_id
FROM tbl_mail_receivers
WHERE email_id = #{emailId}

mapper.update:
UPDATE tbl_mail_receivers
SET trash_status = #{trashStatus}
WHERE email_id = #{emailId}
AND receiver_emp_id = #{receiverEmpId}

UPDATE tbl_mail_receivers
SET trash_status = 0
WHERE email_id = #{emailId}

mapper.select:
SELECT COUNT(*)
FROM tbl_mail
WHERE sender_emp_id = #{empId}
